// AddCoverWalks adds the given related objects to the existing relationships
// of the asset, optionally inserting them as new records.
// Appends related to o.R.CoverWalks.
// Sets related.R.Cover appropriately.
func (o *Asset) AddCoverWalks(ctx context.Context, exec boil.ContextExecutor, insert bool, related ...*Walk) error {
	var err error
	for _, rel := range related {
		if insert {
			queries.Assign(&rel.CoverID, o.ID)
			if err = rel.Insert(ctx, exec, boil.Infer()); err != nil {
				return errors.Wrap(err, "failed to insert into foreign table")
			}
		} else {
			updateQuery := fmt.Sprintf(
				"UPDATE \"walk\" SET %s WHERE %s",
				strmangle.SetParamNames("\"", "\"", 1, []string{"cover_id"}),
				strmangle.WhereClause("\"", "\"", 2, walkPrimaryKeyColumns),
			)
			values := []interface{}{o.ID, rel.ID}

			if boil.IsDebug(ctx) {
				writer := boil.DebugWriterFrom(ctx)
				fmt.Fprintln(writer, updateQuery)
				fmt.Fprintln(writer, values)
			}
			if _, err = exec.ExecContext(ctx, updateQuery, values...); err != nil {
				return errors.Wrap(err, "failed to update foreign table")
			}

			queries.Assign(&rel.CoverID, o.ID)
		}
	}

	if o.R == nil {
		o.R = &assetR{
			CoverWalks: related,
		}
	} else {
		o.R.CoverWalks = append(o.R.CoverWalks, related...)
	}

	for _, rel := range related {
		if rel.R == nil {
			rel.R = &walkR{
				Cover: o,
			}
		} else {
			rel.R.Cover = o
		}
	}
	return nil
}

